css:
html {
    overflow-x: hidden;
}
/* width */
::-webkit-scrollbar {
	width: 5px;
}

/* Track */
::-webkit-scrollbar-track {
	box-shadow: inset 0 0 5px rgb(206, 206, 206);
	border-radius: 10px;
}

/* Handle */
::-webkit-scrollbar-thumb {
	background: rgb(165, 165, 165);
	border-radius: 10px;
}
.print-format-gutter {
    padding: 0px !important;
}
.print-format {
    border-radius: 0px !important;
    max-width: 100% !important;
    min-height: auto !important;
    padding: 4px !important;
    width:100%;
    margin: auto;
}
.accordion-button {
    font-size: 14px;
    padding: 12px;
}
.alert {
    margin-bottom: 0px !important;
    height: 100%;
    display:flex;
    align-items: center;
    justify-content: center;
}
.print-format td, .print-format th {
    padding: 4px !important;
}
.accordion-button:focus {
    box-shadow: none !important;
}
.accordion-button:not(.collapsed),.accordion-button {
    background-color: #e1e1e1 !important;
    color: #000 !important;
}
.empty {
    text-align: center;
    color: #8f8f8f !important;
    padding: 14px !important;
    font-style: italic;
}
.kpi-total-amount {
    background-color: #ffe4c4;
    border: 1px solid #ffd099;
}
.kpi-total-order {
    background: #a6efbc;
    border: 1px solid #43b165;
}
.kpi-total-foc {
    background: #f7bfab;
    border: 1px solid #ffa281;
}
.kpi-total-qty-sold {
    background: #f1f1f1;
    border: 1px solid #cbcbcb;
}
.kpi-total-discount {
    background: #6bd1ff;
    border: 1px solid #4ea4cb;
}
.kpi-total-tax {
    background-color: #ffd4d4;
    border: 1px solid #ffa2a2;
}
.kpi-total-pending-order {
    background-color: #3cb1a6;
    border: 1px solid #008d7f;
    color: #fff;
}
.kpi-total-void {
    background-color: #f57c7c;
    border: 1px solid #ff0000;
    color: #ffffff;
}

.kpi strong {
    font-size: 22px;
}
.kpi .kpi-title {
    font-size: 11px;
    text-transform: uppercase;
    margin-top: -8px;
}
.kpi .kpi-subtitle {
    font-size: 12px;
    text-transform: uppercase;
    margin-top: -2px
}
.table {
    font-size: 14px;
    border-color:#f2f2f2 !important;
}
.table tbody > tr:last-child > td {
  border-bottom: 0;
}
.card-subtitle {
    background: #eaeaea;
    margin: -16px -16px 0px -16px;
    padding: 10px 20px;
    font-size: 16px;
}
.wrap-kpi > div {
    padding-bottom: 16px;
}
