  "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": " \r\n<h4 class=\"text-center\">{{_(\"Stock Take\")}}</h4>\r\n<div class=\"text-center\" style=\"text-transform: uppercase;\">\r\n        {% if doc.docstatus == 0 %}\r\n            <span>Draft </span>\r\n        {% endif %}\r\n        {% if doc.docstatus == 1%}\r\n            <span>Submitted </span>\r\n        {% endif %}\r\n        {% if doc.docstatus == 2 %}\r\n            <span>Cancelled </span>\r\n        {% endif %}\r\n</div>\r\n<div class=\"row\">\r\n    <div class=\"col-xs-4\">\r\n                <table class=\"tbl-list\">\r\n            <tr>\r\n                <td>Stock Take #</td>\r\n                <td>:</td>\r\n                <td>{{doc.name}}</td>\r\n            </tr>\r\n            <tr>\r\n                <td>Date</td>\r\n                <td>:</td>\r\n                <td>{{doc.posting_date}}</td>\r\n            </tr>\r\n        </table>\r\n    </div>\r\n\r\n    <div class=\"col-xs-4\">\r\n        \r\n    </div>\r\n    <div class=\"col-xs-4\">\r\n        <table class=\"tbl-list ml-auto tbl-list-right\">\r\n            <tr>\r\n                <td>Branch</td>\r\n                <td>:</td>\r\n                <td>{{doc.business_branch}}</td>\r\n            </tr>\r\n            <tr>\r\n                <td>Stock Location</td>\r\n                <td>:</td>\r\n                <td>{{doc.stock_location}}</td>\r\n            </tr>\r\n        </table>\r\n        </div>\r\n    </div>"}, {"fieldtype": "Section Break", "label": "Product List"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<table class=\"table table-bordered report-list\">\n    <thead>\n        <tr>\n            <th>No</th>\n            <th>Description</th>\n            <th class=\"text-center\">QTY</th>\n            <th class=\"text-center\">Unit</th>\n            <th class=\"text-right\">Cost</th>\n            <th class=\"text-right\">Total Cost</th>\n        </tr>\n    </thead>\n    <tbody>\n         {% for p in doc.stock_take_products %}\n        <tr>\n            <td>{{p.idx}}</td>\n            <td>{{p.product_code}} - {{p.product_name}}</td>\n            <td class=\"text-center\">{{p.quantity}}</td>\n            <td class=\"text-center\">{{p.unit}}</td>\n            <td class=\"text-right\">{{frappe.utils.fmt_money(p.price,currency=\"$\")}}</td>\n            <td class=\"text-right\">{{frappe.utils.fmt_money(p.amount,currency=\"$\")}}</td>\n        </tr>\n        {% endfor %}\n    </tbody>\n</table>"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div class=\"tbl-list-summary\">\r\n    <table class=\"ml-auto\">\r\n        <tr>\r\n            <td>Total Quantity</td>\r\n            <td>:</td>\r\n            <td>{{doc.total_quantity}}</td>\r\n        </tr>\r\n        <tr>\r\n            <td>Total Cost</td>\r\n            <td>:</td>\r\n            <td>{{ frappe.utils.fmt_money(doc.total_amount,currency=\"$\") }}</td>\r\n        </tr>\r\n    </table>\r\n</div>"}]",
