  "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<h4 class=\"text-center\">{{_(\"Purchase Order Payment\")}}</h4>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div class=\"row\">\r\n \r\n<div class=\"col-xs-6\">\r\n    <div class=\"tbl-list\">\r\n        <div >\r\n            {% set vendor = frappe.get_doc('Vendor',doc.vendor,['*']) %}\r\n            <h4>Vendor Information</h4>\r\n            <div>Vendor Code : {{vendor.name}}</div>\r\n            <div>Vendor Name : {{vendor.vendor_name}}</div>\r\n            {% if vendor.company %}\r\n            <div>Company : {{vendor.company}}</div>\r\n            {% endif %}\r\n            {% if vendor.phone_number %}\r\n            <div>Phone Number : {{vendor.phone_number}}</div>\r\n            {% endif %}\r\n            \r\n            </div>\r\n    </div>\r\n</div>\r\n{% set po = frappe.get_doc('Purchase Order',doc.purchase_order,['*']) %}\r\n<div class=\"col-xs-6\">\r\n    <div class=\"tbl-list\">\r\n        <div class=\"text-right\">\r\n        <h4>Purchase Order Information</h4>\r\n        <p></p>\r\n        <div>PO #: {{po.name}}</div>\r\n        <div>Date: {{po.posting_date}}</div>\r\n        <div>Sub Total: {{frappe.utils.fmt_money(po.sub_total,currency=\"$\")}}</div>\r\n        {% if po.total_discount > 0 %}\r\n        <div>Total Discount: {{frappe.utils.fmt_money(po.total_discount,currency=\"$\")}}</div>\r\n        {% endif %}\r\n        <div>Grand Total: {{frappe.utils.fmt_money(po.grand_total,currency=\"$\")}}</div>\r\n        <div>Total Paid: {{frappe.utils.fmt_money(po.total_paid,currency=\"$\")}}</div>\r\n        {% if po.balance > 0 %}\r\n        <div>Balance: {{frappe.utils.fmt_money(po.balance,currency=\"$\")}}</div>\r\n        {% endif %}\r\n        </div>\r\n    </div>\r\n</div>\r\n<h3 class=\"text-center\">Payment</h3>\r\n    <p></p>\r\n    <div class=\"col-xs-6\">\r\n        <div>Purchase Order Number :</div>\r\n        <div>Posting Date :</div>\r\n        <div>Payment Type :</div>\r\n        <div>Payment Amount :</div>\r\n    </div>\r\n    <div class=\"col-xs-6\">\r\n        <div class=\"tbl-list\">\r\n            <div class=\"text-right\">\r\n                <div>{{doc.name}}</div>\r\n                <div>{{doc.posting_date}}</div>\r\n                <div>{{doc.payment_type}}</div>\r\n                <div>{{frappe.utils.fmt_money(doc.payment_amount,currency=\"$\")}}</div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div class=\"row\" style=\"padding-top:200px\">\n    <div class=\"col-xs-6\">\n        <div class=\"text-center\">\n            <div style=\"margin: 0px auto; border-bottom: 1px solid #000; max-width: 250px\"></div>\n            <div class=\"mt-2\">Seller</div>\n        </div>\n    </div>\n <div class=\"col-xs-6\">\n        <div class=\"text-center\">\n            <div style=\"margin: 0px auto; border-bottom: 1px solid #000; max-width: 250px\"></div>\n            <div class=\"mt-2\">Buyer</div>\n        </div>\n    </div>\n</div>"}]",
