  "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<h4 class=\"text-center\">{{_(\"Sale Payment Receipt\")}}</h4>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div class=\"row\">\r\n    {% set customer = frappe.get_doc('Customer',doc.customer['*']) %}\r\n    {% if customer %}\r\n    <div class=\"col-xs-6\">\r\n        <h4>Customer Information</h4>\r\n        <p></p> \r\n        <table class=\"tbl-list\">\r\n            <tr>\r\n                <td>Customer Code</td>\r\n                <td>:</td>\r\n                <td>{{customer.name}}</td>\r\n            </tr>\r\n            <tr>\r\n                <td>Customer Name</td>\r\n                <td>:</td>\r\n                <td>{{customer.customer_name_en}}</td>\r\n            </tr>\r\n            {% if customer.company %}\r\n            <tr>\r\n                <td>Company</td>\r\n                <td>:</td>\r\n                <td>{{customer.company}}</td>\r\n            </tr>\r\n            {% endif %}\r\n            {% if customer.phone_number %}\r\n            <tr>\r\n                <td>Phone Number</td>\r\n                <td>:</td>\r\n                <td>{{customer.phone_number}}</td>\r\n            </tr>\r\n            {% endif %}\r\n        </table>\r\n    </div>\r\n    {% endif %}\r\n    {% set po = frappe.get_doc('Sale',doc.sale,['*']) %}  \r\n    <div class=\"col-xs-6\">\r\n        <div class=\"tbl-list\">\r\n            <div class=\"text-right\">\r\n                <h4>Sale Information</h4>\r\n                <p></p> \r\n                <div>Sale # : {{po.name}}</div>\r\n                <div>Date : {{po.posting_date}}</div>\r\n                <div>Grand Total : {{frappe.utils.fmt_money(po.grand_total,currency=\"$\")}}</div>\r\n                {% if po.total_tax > 0 %}\r\n                <div>Total Tax : {{frappe.utils.fmt_money(po.total_tax,currency=\"$\")}}</div>\r\n                {% endif %}\r\n                <div>Sub Total : {{frappe.utils.fmt_money(po.sub_total,currency=\"$\")}}</div>\r\n                {% if po.total_discount > 0 %}\r\n                <div>Total Discount : {{frappe.utils.fmt_money(po.total_discount,currency=\"$\")}}</div>\r\n                {% endif %}\r\n                <div>Total Paid : {{frappe.utils.fmt_money(po.total_paid,currency=\"$\")}}</div>\r\n                {% if po.balance > 0 %}\r\n                <div>Balance : {{frappe.utils.fmt_money(po.balance,currency=\"$\")}}</div>\r\n                {% endif %}\r\n                \r\n            </div>\r\n        </div>\r\n    </div>\r\n    <h3 class=\"text-center\">Payment</h3>\r\n    <div class=\"col-xs-6\">\r\n        <div class=\"tbl-list\">\r\n            <div>\r\n                <div>Sale Payment Number :</div>\r\n                <div>Posting Date :</div>\r\n                <div>Payment Type :</div>\r\n                <div>Payment Amount :</div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n    <div class=\"col-xs-6\">\r\n        <div class=\"tbl-list\">\r\n            <div class=\"text-right\">\r\n                <div>{{doc.name}}</div>\r\n                <div>{{doc.posting_date}}</div>\r\n                <div>{{doc.payment_type}}</div>\r\n                <div>{{frappe.utils.fmt_money(doc.payment_amount,currency=\"$\")}}</div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div class=\"row\" style=\"padding-top:200px\">\n    <div class=\"col-xs-6\">\n        <div class=\"text-center\">\n            <div style=\"margin: 0px auto; border-bottom: 1px solid #000; max-width: 250px\"></div>\n            <div class=\"mt-2\">Seller</div>\n        </div>\n    </div>\n <div class=\"col-xs-6\">\n        <div class=\"text-center\">\n            <div style=\"margin: 0px auto; border-bottom: 1px solid #000; max-width: 250px\"></div>\n            <div class=\"mt-2\">Buyer</div>\n        </div>\n    </div>\n</div>"}]",
