  "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<center>\n    <h4>{{_(\"Cashier Shift Sale Transaction\")}}</h4>\n    <h5>{{doc.name}}</h5>\n</center>"}, {"fieldtype": "Section Break", "label": "Shift Information"}, {"fieldtype": "Column Break"}, {"fieldname": "working_day", "print_hide": 0, "align": "right", "label": "Working Day"}, {"fieldname": "business_branch", "print_hide": 0, "align": "right", "label": "Business Branch"}, {"fieldname": "outlet", "print_hide": 0, "align": "right", "label": "Outlet"}, {"fieldname": "pos_profile", "print_hide": 0, "align": "right", "label": "POS Profile"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Created Date:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{frappe.format(doc.creation,{'fieldtype':'Datetime'})}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Opened By:\")}} </label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{ frappe.get_doc(\"User\", doc.owner).full_name}}\n    </div>\n</div>"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{%if doc.is_closed %}\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Status\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{_(\"Closed\")}}\n    </div>\n</div>\n<div class=\"row  data-field\" data-fieldname=\"outlet\" data-fieldtype=\"Data\">\n    <div class=\"col-xs-5\">\n        <label>{{_(\"Closed Date:\")}}</label>\n    </div>\n    <div class=\"col-xs-7 text-right value\">\n        {{frappe.format(doc