      <td class='text-right'>{{frappe.format_value(s.grand_total,\"Currency\")}}</td>\n                    <td>{{s.created_by}}</td>\n                </tr>\n           {% endfor %}\n            <tr>\n                 <td colspan=\"1\" class=\"text-right\"><strong>{{_(\"Total\")}} {{sale_type}}</strong></td>\n                <td class=\"text-center\"><strong>{{sale_transactions | selectattr(\"sale_type\", \"equalto\", sale_type) | list | length}}</strong></td>\n                <td></td>\n                <td class=\"text-center\"> <strong>{{sale_transactions |selectattr(\"sale_type\", \"equalto\", sale_type) |sum(attribute='total_quantity')}}</strong></td>\n                <td align=\"right\"><strong>{{frappe.format_value(sale_transactions|selectattr(\"sale_type\", \"equalto\", sale_type)| sum(attribute='grand_total'),\"Currency\")}} </strong></td>\n            </tr>\n        \n        {% endfor %}\n        \n    </tbody>\n    <tfoot>\n        <tr>\n                <td colspan=\"1\" class=\"text-right\"><strong>{{_(\"Total\")}}</strong></td>\n                <td class=\"text-center\"><strong>{{sale_transactions| count }}</strong></td>\n                <td> </td>\n                <td class=\"text-center\"><strong>{{sale_transactions |sum(attribute='total_quantity')}}</strong></td>\n                <td class=\"text-right\"><strong>{{frappe.format_value(sale_transactions | sum(attribute='grand_total'),\"Currency\")}}</strong></td>\n            </tr>\n    </tfoot>\n</table>\n"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% \n    set sale_transactions = frappe.db.sql(\"select sale_type,name,tbl_number,TIME_FORMAT(creation, '%H:%i') as time, total_quantity,grand_total,created_by,customer_name from `tabSale` where cashier_shift = '{}' and docstatus = 1 and is_foc = 1\".format(doc.name),as_dict=1)\n%}\n{% if sale_transactions %}\n<h4 class=\"col-sm-12\" style=\"padding-left:0px\">{{_(\"FOC\")}}</h4>\n\n<table class