       \n        {% endfor %}\n        \n    </tbody>\n    <tfoot>\n        <tr>\n                <td colspan=\"1\" class=\"text-right\"><strong>{{_(\"Total\")}}</strong></td>\n                <td class=\"text-center\"><strong>{{sale_transactions| count }}</strong></td>\n                <td> </td>\n                <td class=\"text-center\"><strong>{{sale_transactions |sum(attribute='total_quantity')}}</strong></td>\n                <td class=\"text-right\"><strong>{{frappe.format_value(sale_transactions | sum(attribute='grand_total'),\"Currency\")}}</strong></td>\n            </tr>\n    </tfoot>\n</table>\n\n{%endif%}\n"}]",
