  "format_data": "[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "{%\n    set sql = \"\n        select \n            sum(if(is_foc=1 or docstatus!=1,0,grand_total)) as total_revenue ,\n            coalesce(sum(if(is_foc=1 or docstatus!=1,0,1)),0) as total_order,\n            sum(if(is_foc=1 and docstatus=1,grand_total,0)) as total_foc,\n            sum(if(is_foc=1 and docstatus=1,1,0)) as total_foc_order,\n            coalesce(sum(if(is_foc=1 or docstatus!=1,0,total_quantity)),0) as total_quantity,\n            coalesce(sum(if(is_foc=1 and docstatus=1,total_quantity,0)),0) as total_foc_quantity,\n            sum(if(is_foc=1 and docstatus=1,0,total_discount)) as total_discount,\n            sum(if(is_foc=1 and docstatus=1,0,total_tax)) as total_tax,\n            sum(if( docstatus=0,grand_total,0)) as pending_amount,\n            sum(if( docstatus=0,1,0)) as pending_order,\n            ifnull(sum(if( docstatus=2,1,0)),0) as total_void\n            \n        from `tabSale`\n        where\n            working_day = if('{0}'='',working_day,'{0}') and \n            cashier_shift = if('{1}'='',cashier_shift,'{1}') and \n            ifnull(outlet,'') = if('{2}'='',ifnull(outlet,''),'{2}') and\n            ifnull(tbl_group,'') = if('{3}'='',ifnull(tbl_group,''),'{3}')\n            \".format(\n                (frappe.form_dict.get(\"working_day\") or \"\"),\n                (frappe.form_dict.get(\"cashier_shift\") or \"\"),\n                (frappe.form_dict.get(\"outlet\") or \"\"),\n                (frappe.form_dict.get(\"table_group\") or \"\"),\n            )\n   \n\n%}\n{% \n    set data = frappe.db.sql(sql,as_dict=1)\n%}\n<div class=\"container-fluid\" style=\"padding:0px;\">\n    <div class=\"row wrap-kpi\">\n        <div class=\"col-sm-4 col-6 col-lg-3\">\n            \n            <div class=\"alert kpi kpi-total-amount text-center\">\n                <div>\n                    <strong>{{frappe.format_value(data[0].total_revenue,\"Cur